CREATE TABLE IF NOT EXISTS GAME (id identity not null primary key, gameid bigint not null, player varchar(255) not null, deck varchar(255) not null, win boolean not null, kills int not null default 0, keycards varchar(255) not null, artifacts int not null default 0, creatures int not null default 0, planeswalkers int not null default 0, enchantments int not null default 0, instants int not null default 0, sorceries int not null default 0, turns int not null default 0, notes varchar(255) not null)